import React from 'react';
import './App.css';
import { CATEGORIES } from './data'

class App extends React.Component {

  state = {
    tasks: [
      {
        text: 'Buy rice',
        category: 'Food'
      },
      {
        text: 'Save a tenner',
        category: 'Money'
      },
      {
        text: 'Build a todo app',
        category: 'Code'
      },
      {
        text: 'Build todo API',
        category: 'Code'
      },
      {
        text: 'Get an ISA',
        category: 'Money'
      },
      {
        text: 'Cook rice',
        category: 'Food'
      },
      {
        text: 'Tidy house',
        category: 'Misc'
      }
    ]
  }

  render() {
    return (
      <div className="App">
        <h2>My tasks</h2>
        <h5>Categories Filters</h5>
      <div className='categories'>
        <button>All</button>
        <button>Food</button>
        <button>Money</button>
        <button>Code</button>
        <button>Misc</button>
      </div>
      <div className='tasks'></div>
      <h5>Tasks</h5>
        <div className='task'></div>


      </div>
    );
  }
}


export default App;
